INSERT INTO books (ISBN, image, title, languages, keywords, publisher, page_num, summary) VALUES
(9780747574494,  'https://m.media-amazon.com/images/I/91VsMogZpwL._AC_UY327_FMwebp_QL65_.jpg', 'Harry Potter and the Goblet of Fire', 'English', 'Fantasy, Magic, Adventure', 'Bloomsbury Publishing', 640, 'The Triwizard Tournament is to be held at Hogwarts. Only wizards who are over seventeen are allowed to enter - but that doesnt stop Harry dreaming that he will win the competition. Then at Halloween, when the Goblet of Fire makes its selection, Harry is amazed to find his name is one of those that the magical cup picks out. He will face death-defying tasks, dragons and Dark wizards, but with the help of his best friends, Ron and Hermione, he might just make it through - alive!');
INSERT INTO books (ISBN, image, title, languages, keywords, publisher, page_num, summary) VALUES
(9780439139601,  'https://m.media-amazon.com/images/I/91II+F1H7LL._AC_UY327_FMwebp_QL65_.jpg', 'Harry Potter and the Prisoner of Azkaban', 'English', 'Fantasy, Magic, Adventure', 'Bloomsbury Publishing', 480, 'For twelve long years, the dread fortress of Azkaban held an infamous prisoner named Sirius Black. Convicted of killing thirteen people with a single curse, he was said to be the heir apparent to the Dark Lord, Voldemort. Now he has escaped, leaving only two clues as to where he might be headed: Harry Potters defeat of You-Know-Who was Blacks downfall as well. And the Azkaban guards heard Black muttering in his sleep, "Hes at Hogwarts...hes at Hogwarts."');
INSERT INTO books (ISBN, image, title, languages, keywords, publisher, page_num, summary) VALUES
