INSERT INTO books (ISBN, image, title, languages, keywords, publisher, page_num, summary) VALUES
(9780061120084,  'https://m.media-amazon.com/images/I/71fFOMJM41L._AC_UY327_FMwebp_QL65_.jpg', 'Twilight', 'English', 'fantasy, romance, vampires', 'Little, Brown and Company', 498, 'About three things I was absolutely positive. First, Edward was a vampire. Second, there was a part of him -and I didnt know how dominant that part might be -that thirsted for my blood. And third, I was unconditionally and irrevocably in love with him.');
INSERT INTO books (ISBN, image, title, languages, keywords, publisher, page_num, summary) VALUES
(9781400079988, 'https://m.media-amazon.com/images/I/81RUfP0ZOjL._AC_UY327_FMwebp_QL65_.jpg', 'The Kite Runner', 'English', 'drama, historical fiction, war', 'Riverhead Books', 371, 'The unforgettable, heartbreaking story of the unlikely friendship between a wealthy boy and the son of his father s servant, caught in the tragic sweep of history, The Kite Runner transports readers to Afghanistan at a tense and crucial moment of change and destruction.');
INSERT INTO books (ISBN, image, title, languages, keywords, publisher, page_num, summary) VALUES
(9780525555360, 'https://m.media-amazon.com/images/I/714n3v0MGrL._AC_UY327_FMwebp_QL65_.jpg', 'The Wasp Factory', 'English', 'Psychological thriller, Coming-of-age', 'Random House', 334, 'The Wasp Factory is a dark and gripping psychological thriller that follows the disturbing life of Frank, a young protagonist with a penchant for dangerous and macabre rituals. Set in a remote Scottish island, this coming-of-age novel explores themes of identity, family secrets, and the blurred boundaries between sanity and madness.');
INSERT INTO books (ISBN, image, title, languages, keywords, publisher, page_num, summary) VALUES
(9780451476169,  'https://m.media-amazon.com/images/I/81RCNdgGHcL._AC_UY327_FMwebp_QL65_.jpg', 'Obsession in Death', 'English', 'crime, thriller, fiction', 'Berkley Books', 416, 'Lieutenant Eve Dallas walks the thin line between love and hate in the 40th In Death thriller from #1 New York Times bestselling author J. D. Robb. Eve Dallas has solved a lot of high profile murders for the NYPSD and gotten a lot of media. She and her billionaire husband are getting accustomed to being objects of attention of gossip of speculation. But now Eve has become the object of one persons obsession. Someone who finds her extraordinary, and thinks about her every hour of every day. Who believes the two of them have a special relationship. Who would kill for her -again and again… With a murderer reading meanings into her every move, handling this case will be a delicate -and dangerous -psychological dance. And Eve knows that underneath the worship and admiration, a terrible threat lies in wait. Because the beautiful lieutenant is not at all grateful for these bloody offerings from her “true and loyal friend.” And in time, idols always fall…');
INSERT INTO books (ISBN, image, title, languages, keywords, publisher, page_num, summary) VALUES
(9780802714636,  'https://m.media-amazon.com/images/I/51J1mtgK+yL._AC_UY327_FMwebp_QL65_.jpg', 'The Science of Liberty: Democracy, Reason, and the Laws of Nature', 'English', 'science, philosophy, politics', 'Grove Press', 416, 'Renowned science writer Timothy Ferris examines the competing notions of science and democracy, arguinng that Western sciences empirical and quantitative strengths may also be its greatest weakness');
INSERT INTO books (ISBN, image, title, languages, keywords, publisher, page_num, summary) VALUES
(9781591847626,  'https://m.media-amazon.com/images/I/81p0X7GO9xL._AC_UY327_FMwebp_QL65_.jpg', 'How We Got to Now: Six Innovations That Made the Modern World', 'English', 'innovation, technology, history', 'Riverhead Books', 304, 'From the New York Times -bestselling author of Where Good Ideas Come From and Farsighted, a new look at the power and legacy of great ideas. In this illustrated history, Steven Johnson explores the history of innovation over centuries, tracing facets of modern life (refrigeration, clocks, and eyeglass lenses, to name a few) from their creation by hobbyists, amateurs, and entrepreneurs to their unintended historical consequences. Filled with surprising stories of accidental genius and brilliant mistakes -from the French publisher who invented the phonograph before Edison but forgot to include playback, to the Hollywood movie star who helped invent the technology behind Wi-Fi and Bluetooth -How We Got to Now investigates the secret history behind the everyday objects of contemporary life.');
